(()=>{const a=document.body,b=document.getElementById("mode"),c=b=>{b==="dark"?(localStorage.setItem("theme","light"),a.removeAttribute("data-theme")):b==="light"?(localStorage.setItem("theme","dark"),a.setAttribute("data-theme","dark")):initTheme(b)};b.addEventListener("click",()=>c(localStorage.getItem("theme")));const d=document.getElementById("menu-trigger");d.addEventListener("change",function(){const a=document.querySelector(".wrapper");this.checked?a.classList.add("blurry"):a.classList.remove("blurry")})})()